(function() {
    function injectScript(file, id, node) {
        if (node.querySelector('#' + id)) {
            return;
        };
        var script = document.createElement("script");
        script.src = file;
        script.id = id;
        node.appendChild(script);
    }
    var script = document.querySelector('script[src*="assets/lazy-summaries/lazy-summaries.min.js"]');
    if(script){
    	injectScript('https://assets.squarewebsites.org/lazy-summaries/lazy-summaries.min.js', 'lazy-summaries', script.parentNode);
    }
})();